import React, { useState } from "react";
import ReactQuill from "react-quill";
import { useNavigate } from "react-router-dom";
import "./../styles/Editor.css"


const Editor = (item, save, Delete) => {


  console.log("6")

  // const navigate = useNavigate();


  console.log("7")

  const modules = {
    toolbar: [
      [{ header: [1, 2, 3, 4, 5, 6, false] }],
      ["bold", "italic", "underline", "strike", "blockquote"],
      [{ font: [] }],
      [{ align: ["right", "center", "justify"] }],
      [{ list: "ordered" }, { list: "bullet" }],
      ["link", "image"],
      [{ color: [] }],
      [{ background: [] }]
    ]
  }


  console.log("8")

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "link",
    "color",
    "image",
    "background",
    "align",
    "font"
  ]


  console.log("9")

  // const [code, setCode] = useState(item.text);

  // function save() {
  //   navigate(`/notes/0`)
  // }

  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
        return "";
    }
    return formatted;
  };

  return (
    <>  
      <div className="viewer-header">
        <div className="viewer-header-left">
          <div className="viewer-title">{item.title}</div>
          <div className="viewer-date">
            <input type="datetime-local" autoComplete="on"/> 
          </div>

        </div>
        <div className="viewer-header-right">
          <button className="viewer-edit-button" onClick={() => save()}>Save</button>
          <button className="viewer-delete-button" onClick={() => Delete()}>Delete</button>
        </div>
      </div>
      <ReactQuill
        theme="snow"
        modules={modules}
        formats={formats}
        value={item.text}
        // value={code}
        // onChange={setCode}
      />
    </>
  )
}

export default Editor;
