import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect, useRef} from 'react';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';



function Home() {

  function makeElements(item) {
    return <NotePreview title={item.title} preview={item.uuid}></NotePreview>
  }

  const notes = [{text: "Text", title: "Title", uuid: "4"}]


  const [noteList, setNoteList] = useState( { data: [
                                                      { 
                                                        text: "Text",
                                                        title: "Title",
                                                        uuid: "1"
                                                      },
                                                      {
                                                        text: "Text",
                                                        title: "Title",
                                                        uuid: "2"
                                                      },
                                                      {
                                                        text: "Text",
                                                        title: "Title",
                                                        uuid: "3"
                                                      }
                                                    ]
                                            }
                                          )
  const elements = useRef([])

  useEffect(()=>{
    console.log(elements.current)
    console.log(noteList.map(makeElements))
  }, [noteList])

  function AddNewNote(text, title, id) {
    noteList.push({
                    text: text,
                    title: title,
                    uuid: id
                  })
                  console.log(noteList)
  }

  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();
  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          {/* <button onClick={() => AddNewNote(notes.text, notes.title, elementID)}>+</button> */}
          <button onClick={() => {
                                    setNoteList(noteList.push(notes[0]))
                                    console.log("click")
                                    console.log(notes)
                                    console.log(noteList)
                                    console.log(elements)
                                 }}>+</button>
        </div>
        <div className='navigation-items'>
          {elements.current}
        </div>
      </div>
      <div className='content-container'>{Editor("hello there")}</div>
    </div>
  );
}

export default Home;
