import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect, useRef} from 'react';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';



function Home() {

  function makeElements(item) {
    console.log(`makeElements 1 element: ${elements.length}, notes: ${noteList.length}`)
    return <NotePreview title={item.title} preview={item.uuid}></NotePreview>
  }


  const notes = [{text: "Text", title: "Title", uuid: "4"}]


  const [noteList, setNoteList] = useState( [
                                              { 
                                                text: "Text",
                                                title: "Title",
                                                uuid: "1"
                                              },
                                              {
                                                text: "Text",
                                                title: "Title",
                                                uuid: "2"
                                              },
                                              {
                                                text: "Text",
                                                title: "Title",
                                                uuid: "3"
                                              }
                                            ]

                                          )
  const elements = useRef()
  const [, setForceUpdate] = useState(Date.now())
  useEffect(()=>{
    console.log(`Effect 1 element: ${elements.current.length}, notes: ${noteList.length}`)
    elements.current = noteList.map(makeElements)
    console.log(`Effect 2 element: ${elements.current.length}, notes: ${noteList.length}`)
  }, [noteList])



  function AddNewNote(id) {
    console.log(`AddNewNote 1 element: ${elements.length}, notes: ${noteList.length}`)
    setNoteList([...noteList, {text: "Text", title: "Title", uuid: id}])
    console.log(`AddNewNote 2 element: ${elements.length}, notes: ${noteList.length}`)
  }

  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();
  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          <button onClick={() => {
            console.log(`OnClick 1 element: ${elements.length}, notes: ${noteList.length}`)
            AddNewNote(uuidv4())
            console.log(`OnClick 2 element: ${elements.length}, notes: ${noteList.length}`)
            }}>+</button>
          {/* <button onClick={() => {  
                                    setNoteList([...noteList, {text: "Text", title: "Title", uuid: "4"}])
                                    console.log("after click")
                                    console.log(noteList)
                                 }}>+</button> */}
        </div>
        <div className='navigation-items'>
          {elements.current}
        </div>
      </div>
      <div className='content-container'>{Editor("hello there")}</div>
    </div>
  );
}

export default Home;
