import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect, useRef} from 'react';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';



function Home() {

  function makeElements(item) {
    return <NotePreview title={item.title} preview={item.uuid}></NotePreview>
  }


  const notes = [{text: "Text", title: "Title", uuid: "4"}]


  const elements = useRef([])
  const [noteList, setNoteList] = useState( [
                                              { 
                                                text: "Text",
                                                title: "Title",
                                                uuid: "1"
                                              },
                                              {
                                                text: "Text",
                                                title: "Title",
                                                uuid: "2"
                                              },
                                              {
                                                text: "Text",
                                                title: "Title",
                                                uuid: "3"
                                              }
                                            ]

                                          )

  useEffect(()=>{
    console.log("Effect")
    console.log(elements.current)
    console.log(noteList)
    elements.current = noteList.map(makeElements)
  }, [noteList])


  function AddNewNote(id) {
    console.log(`click befor element: ${elements.current}, notes: ${noteList}`)
    setNoteList([...noteList, {text: "Text", title: "Title", uuid: id}])
  }

  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();
  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          <button onClick={() => {AddNewNote(uuidv4())}}>+</button>
          {/* <button onClick={() => {  
                                    setNoteList([...noteList, {text: "Text", title: "Title", uuid: "4"}])
                                    console.log("after click")
                                    console.log(noteList)
                                 }}>+</button> */}
        </div>
        <div className='navigation-items'>
          {elements.current}
        </div>
      </div>
      <div className='content-container'>{Editor("hello there")}</div>
    </div>
  );
}

export default Home;
