import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect, useRef, useNavigate} from 'react';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';
import Viewer from '../elements/Viewer';



function Home() {

  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();

  function makeElements(item) {
    return <NotePreview text={item.text} title={item.title} date={item.date}></NotePreview>
  }

  const [noteList, setNoteList] = useState( 
    [
      { 
        text: "<< Place Holder Text >>", 
        title: "Untitled", 
        uuid: "PreSet01", 
        date: "No Selected Date"
      },
      {
        text: "<< Place Holder Text >>", 
        title: "Untitled", 
        uuid: "PreSet02", 
        date: "No Selected Date"
      },
      {
        text: "<< Place Holder Text >>", 
        title: "Untitled", 
        uuid: "PreSet03", 
        date: "No Selected Date"
      }
    ]
  )

  function AddNewNote() {
    setNoteList([...noteList, {
      text: "<< Place Holder Text >>", 
      title: "Untitled", 
      uuid: uuidv4(), 
      date: "No Selected Date"
    }])
  }

  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          <button onClick={() => {AddNewNote()}}>+</button>
        </div>
        <div className='navigation-items'>
          {noteList.map(makeElements)}
        </div>
      </div>
      {/* <div className='content-container'>{Editor("hello there")}</div> */}
      {/* <div className='content-container'>{Viewer(noteList[1])}</div> */}
    </div>
  );
}

export default Home;
