import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import ReactQuill, { Quill } from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';
import NewNote from './../elements/NewNote';




function AddNewNote(notes, index) {
  const noteElement = document.createElement("NotePreview")
  noteElement.setAttribute("title", "Title")
  noteElement.setAttribute("preview", "preview")
  console.log(noteElement)
  // return noteElement
}




function Home() {

  // const [displayNotes, setDisplayNotes] = useState(0);
  // useEffect(()=>{
  //   let x =0
  // }, [displayNotes])

  const notes = [{title: "Title", text: "Text", uuid: "1"}]
  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();
  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          <button onClick={AddNewNote(notes, elementID)}>+</button>
        </div>
        <div className='navigation-items'>
          <NotePreview title="Title" preview="Preview"></NotePreview>
        </div>
      </div>
      <div className='content-container'>{Editor("hello there")}</div>
    </div>
  );
}

export default Home;
