import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';
import NewNote from '../elements/Elements';



function Home() {

  function makeElements(item) {
    return <NotePreview title={item.title} preview={item.uuid}></NotePreview>
  }

  const [noteList, setNoteList] = useState([{ 
                                            text: "Text",
                                            title: "Title",
                                            uuid: "1"
                                          },
                                          {
                                            text: "Text",
                                            title: "Title",
                                            uuid: "2"
                                          },
                                          {
                                            text: "Text",
                                            title: "Title",
                                            uuid: "3"
                                          }]  )

  let elements = noteList.map(makeElements)

  useEffect(()=>{
    elements = noteList.map(makeElements)
  }, [noteList])

  function AddNewNote(text, title, id) {
    noteList.push({
                    text: text,
                    title: title,
                    uuid: id
                  })
                  console.log(noteList)
  }

  const notes = [{title: "Title", text: "Text", uuid: "1"}]
  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();
  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          {/* <button onClick={() => AddNewNote(notes.text, notes.title, elementID)}>+</button> */}
          <button onClick={() => setNoteList(noteList + {text: "Text", title: "Title", uuid: "4"})}>+</button>
        </div>
        <div className='navigation-items'>
          {elements}
        </div>
      </div>
      <div className='content-container'>{Editor("hello there")}</div>
    </div>
  );
}

export default Home;
