import '../styles/Home.css';
import { useParams } from "react-router-dom";
import NotePreview from "./../elements/NotePreview";
import { useOutletContext } from "react-router-dom";
import React, { useState, useEffect, useRef, useNavigate} from 'react';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import Editor from './../elements/Editor';
import Viewer from '../elements/Viewer';



function Home() {

  const { elementID, mode } = useParams();
  const [navigationBarState] = useOutletContext();
  const [body, setBody] = useState(<>"PlacesHolder"</>)

  // useEffect(()=>{
  //   console.log("useEffect")
  //   if (mode === "edit") {
  //     console.log("edit")
  //     setBody(<div className='content-container'>{Editor(noteList[parseInt(elementID)])}</div>)
  //   } 
  //   else {
  //     console.log("view")
  //     setBody(<div className='content-container'>{Viewer(noteList[parseInt(elementID)])}</div>)
  //   }
  // }, [elementID, mode])

  function getBody(noteList, id, mode) {
    if (mode == "edit") {
      return Viewer(noteList[parseInt(id)])
    } else {
      return Viewer(noteList[parseInt(id)])
    }

  }

  useEffect(()=>{
    console.log("useEffect")
    setBody(<div className='content-container'>{getBody(noteList, elementID, mode)}</div>)
  }, [elementID, mode])

  function makeElements(item) {
    return <NotePreview text={item.text} title={item.title} date={item.date}></NotePreview>
  }

  const [noteList, setNoteList] = useState( 
    [
      { 
        text: "<< Place Holder Text >>", 
        title: "Untitled", 
        uuid: "PreSet01", 
        date: "No Selected Date"
      },
      {
        text: "<< Place Holder Text >>", 
        title: "Untitled", 
        uuid: "PreSet02", 
        date: "No Selected Date"
      },
      {
        text: "<< Place Holder Text >>", 
        title: "Untitled", 
        uuid: "PreSet03", 
        date: "No Selected Date"
      }
    ]
  )

  function AddNewNote() {
    setNoteList([...noteList, {
      text: "<< Place Holder Text >>", 
      title: "Untitled", 
      uuid: uuidv4(), 
      date: "No Selected Date"
    }])
  }

  return (
    <div className='body'>
      <div className='navigation-bar'>
        <div className='navigation-header'>
          <div>Notes</div>
          <button onClick={() => {AddNewNote()}}>+</button>
        </div>
        <div className='navigation-items'>
          {noteList.map(makeElements)}
        </div>
      </div>
      {/* <div className='content-container'>{Editor(noteList[parseInt(elementID)])}</div>
      <div className='content-container'>{Viewer(noteList[parseInt(elementID)])}</div> */}
      {body}
    </div>
  );
}

export default Home;
