import React, { useState } from "react";
import ReactQuill from "react-quill";
import "./../styles/Editor.css"

export default function Editor(item) {

  const modules = {
    toolbar: [
      [{ header: [1, 2, 3, 4, 5, 6, false] }],
      ["bold", "italic", "underline", "strike", "blockquote"],
      [{ font: [] }],
      [{ align: ["right", "center", "justify"] }],
      [{ list: "ordered" }, { list: "bullet" }],
      ["link", "image"],
      [{ color: [] }],
      [{ background: [] }]
    ]
  }

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "link",
    "color",
    "image",
    "background",
    "align",
    "font"
  ]

  // const [code, setCode] = useState(item.text);

  const SaveNote = () => {
    setNoteList(noteList => [...noteList, {
      text: "<< Place Holder Text >>", 
      title: "Untitled", 
      uuid: uuidv4(), 
      date: "No Selected Date"
    }])
    navigate(`/notes/0/edit`)
  }

  return (
    <>  
      <div className="viewer-header">
        <div className="viewer-header-left">
          <div className="viewer-title">{item.title}</div>
          <div className="viewer-date">{item.date}</div>
        </div>
        <div className="viewer-header-right">
          <button className="viewer-edit-button">Save</button>
          <button className="viewer-delete-button">Delete</button>
        </div>
      </div>
      <ReactQuill
        theme="snow"
        modules={modules}
        formats={formats}
        value={item.text}
        // value={code}
        // onChange={setCode}
      />
    </>
  )
}
